#include &lt;stdio.h&gt;#include &lt;string.h&gt;#include &lt;sys/types.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt; #define MY_PORT 3333  int main(int argc, char *argv[]) { int sockfd, new_fd; struct sockaddr_in my_addr; // hedef adres  struct sockaddr_in their_addr; // baglanti yapan adres  int sin_size, rt; // gelen verinin tutuldugu buffer  char buf[32]; // islem yapmak icin gerekli dosya gosterici  sockfd = socket(AF_INET, SOCK_DGRAM, 0); sin_size = sizeof(struct sockaddr_in); // adres ile ilgili ayarlar  my_addr.sin_family = AF_INET; my_addr.sin_port = htons(MY_PORT); // kullanilacak port  my_addr.sin_addr.s_addr = INADDR_ANY; // mevcut ip adresi  memset(&amp;(my_addr.sin_zero), 0, 8); // geri kalani 0la  // socket ile dosyayi iliskilendir  bind(sockfd, (struct sockaddr *)&amp;my_addr, sizeof(struct sockaddr)); while ( 1 ) { rt = recvfrom (sockfd, buf, 12, 0, (struct sockaddr *)&amp;their_addr, &amp;sin_size); if (rt &gt; 0) { printf (&#34;recv: %s\n&#34;, buf); } else if (rt &lt; 0) { printf(&#34;Baglanti koptu\n&#34;); break; } } close(sockfd); return 0; } İstemci:
#include &lt;stdio.h&gt;#include &lt;string.h&gt;#include &lt;sys/types.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt; #define DEST_IP &#34;255.255.255.255&#34; #define DEST_PORT 3333  int main(int argc, char *argv[]) { int sockfd, new_fd; struct sockaddr_in dest_addr; // hedef adres  int sin_size, rt; sin_size = sizeof(struct sockaddr_in); // islem yapmak icin gerekli dosya gosterici  sockfd = socket(AF_INET, SOCK_DGRAM, 0); // adres ile ilgili ayarlar  dest_addr.sin_family = AF_INET; dest_addr.sin_port = htons(DEST_PORT); dest_addr.sin_addr.s_addr = inet_addr(DEST_IP); memset(&amp;(dest_addr.sin_zero), 0, 8); // geri kalani 0 la  // Broadcast icin gerekli yetkiyi al  int flag = 1; if (setsockopt (sockfd, SOL_SOCKET, SO_BROADCAST, &amp;flag, sizeof(flag)) &lt; 0) perror(&#34;setsockopt&#34;); // mesaji gonder  rt = sendto (sockfd, &#34;slm asl plz&#34;, 12, 0, (const struct sockaddr *)&amp;dest_addr, sin_size); if ( rt &lt; 0 ) perror(&#34;Hata: &#34;); // socketi kapat  close(sockfd); return 0; }

Bir proje için server sockete ihtiyaç duydum. Öncelikli dilim python olmasına rağmen, sunucu solaris olunca java ile yazmak daha cazip geldi. Öncelikli olarak test amaçlı birkaç satır kod yazdım. Akla gelen ilk örnek echo server oluyor :) aşağıda multi thread çalışan, birden fazla kullanıcıya cevap verebilecek bir echo server var:)
Server.java
/** * @author: Aşkın Yollu */ import java.net.*; import java.io.*; class Server { public static void main(String args[]) throws Exception { ServerSocket myService = null; Socket client = null; Worker tmpworker = null; try { // 1234 nolu portu dinle  myService = new ServerSocket(1234); } catch (IOException e) { System.out.println(e); } try { while (true) { // bağlantı kuracak istemcileri bekle  client = myService.accept(); // bağlantıları bir listeye ekle  // istemci için yeni thread oluştur  tmpworker = new Worker(client); } } catch (IOException e) { System.out.println(e); } } } Worker.java
/** * @author: Aşkın Yollu */ import java.net.*; import java.io.*; class Worker implements Runnable { private Socket client; private DataInputStream is; private DataOutputStream os; Thread t; // Yaratıcı method  public Worker(Socket client) { this.client = client; t = new Thread(this); t.start(); } public void run() { String line = null; try { this.is = new DataInputStream(this.client.getInputStream()); this.os = new DataOutputStream(this.client.getOutputStream()); while (true) { line =this.is.readLine(); this.os.writeBytes(line + &#34;\n&#34;); } } catch (IOException e) { System.out.println(e); } } } kodları derleyip çalıştırdıktan sonra
telnet localhost 1234 komutunu verip birşeyler yazmaya başlayın, bakalım ne olacak :)
